import React from "react";
// TODO: import useFormik from formik library
import {useFormik} from 'formik'

function App() {
  // TODO: add a const called formik assigned to useFormik()
  const formik = useFormik({
    initialValues: {
      email: '', 
      password: ''
    },
    onSubmit: values => {
      console.log('form:', values)
    },
    validate: values => {
      let errors = {};
      if(!values.email) errors.email = 'Field required';
      if(!values.password) errors.password = 'Field required';
      return errors;
    }
  });

  return (
    <div id="space"xt>
      {/* <p>
        The app is ready! You can proceed with the task instructions. TODO:
        build you form here.
      </p> */}
      <form onSubmit={formik.handleSubmit}>
        <div>Email</div>
        <input id="emailField" type="text" onChange={formik.handleChange} values={formik.values.email}/>
        {formik.errors.email ? <div id='emailError' style={{color:'red'}}>{formik.errors.email}</div>: null}
        <div>Password</div>
        <input id="pswField" type="text" onChange={formik.handleChange} values={formik.values.password}/>
        {formik.errors.password ? <div id='pswError' style={{color:'red'}}>{formik.errors.password}</div>: null}
        <div><button id="submitBtn" type='submit'>Submit</button></div>

      </form>
    </div>
  );
}

export default App;
